import React, { useState, useEffect } from 'react';
import styles from './PhoneInCall.module.css';

function PhoneInCall() {
    const [isInCall, setIsInCall] = useState(false);

    // Mock function to change call state
    const toggleCallStatus = () => setIsInCall(!isInCall);

    useEffect(() => {
        // Here you can use any APIs available to you that could
        // change the state of isInCall, which is not possible in a 
        // web environment due to privacy and security concerns.
    }, []);

    return (
        <div className={styles.container}>
            <img 
                src="phone-icon.png" 
                alt="Phone Icon" 
                className={styles.phoneIcon}
            />
            <h2 className={styles.callStatus}>
                {isInCall ? 'The device is in call' : 'The device is not in call'}
            </h2>
            <button className={styles.toggleButton} onClick={toggleCallStatus}>Toggle Call Status</button>
        </div>
    );
}

export default PhoneInCall;
