// components/Contacts.js

import React, { useState, useEffect } from 'react';
import styles from './Contacts.module.css'; // Import the CSS module
import { v4 as uuidv4 } from 'uuid';


function Contacts() {
  const [contacts, setContacts] = useState([]);

  useEffect(() => {
    fetch('/contacts.json')
      .then((response) => response.json())
      .then((data) => setContacts(data))
      .catch((error) => console.error('Error fetching contacts:', error));
  }, []);

  const deleteContact = (index) => {
    const updatedContacts = contacts.filter((_, i) => i !== index);
    setContacts(updatedContacts);
  };

  const addContact = () => {
    // Create a new contact object
    const newContact = {
      id: uuidv4(),
      name: 'New Contact',
      email: '',
      phone: '',
    };

    // Prepend the new contact to the existing contacts list
    setContacts([newContact, ...contacts]);
  };

  return (
    <div className={styles.contacts}>
      <h1 className={styles.title}>Contacts</h1>
      <button className={styles.addButton} onClick={addContact}>
        Add Contact
      </button>
      <ul className={styles.contactList}>
        {contacts.map((contact, index) => (
          <li key={index} className={styles.contactItem}>
            {contact.name}{' '}
            <button className={styles.deleteButton} onClick={() => deleteContact(index)}>
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Contacts;
