import React, { useState, useEffect } from 'react';
import styles from './Contacts.module.css';
import { v4 as uuidv4 } from 'uuid';
import { useWorker } from 'react-hooks-worker';
const contactsWorker = new Worker('./ContactWorker.js');

function Contacts() {
  const [contacts, setContacts] = useState([]);
  const [newContactName, setNewContactName] = useState('');

  const addContact = () => {
    if (newContactName.trim() === '') {
      alert('Please enter a contact name.');
      return;
    }

    const newContact = {
      id: uuidv4(),
      name: newContactName,
    };

    setContacts((prevContacts) => [...prevContacts, newContact]);
    setNewContactName('');
  };

  const deleteContact = (id) => {
    setContacts((prevContacts) => prevContacts.filter((contact) => contact.id !== id));
  };

  const { result: sortedContacts } = useWorker(contactsWorker, ['sort', contacts]);
  const { result: duplicatedContacts } = useWorker(contactsWorker, ['duplicate', contacts]);

  useEffect(() => {
    if (duplicatedContacts) {
      setContacts(duplicatedContacts);
    }
  }, [duplicatedContacts]);

  useEffect(() => {
    if (sortedContacts) {
      setContacts(sortedContacts);
    }
  }, [sortedContacts]);

  const duplicateContacts = () => {
    if (contacts.length === 0) {
      alert('No contacts to duplicate.');
      return;
    }

    contactsWorker.postMessage(['duplicate', contacts]);
  };

  const sortContacts = () => {
    contactsWorker.postMessage(['sort', contacts]);
  };

  return (
    <div className={styles.contacts}>
      <h1 className={styles.title}>Contacts</h1>
      <div className={styles.addContactContainer}>
        <input
          className={styles.contactNameInput}
          type="text"
          placeholder="Enter contact name"
          value={newContactName}
          onChange={(e) => setNewContactName(e.target.value)}
        />
        <button className={styles.addContactButton} onClick={addContact}>
          Add Contact
        </button>
        <button data-testid="duplicateContactsButton" className={styles.duplicateButton} onClick={duplicateContacts}>
          Duplicate Contacts
        </button>
      </div>
      <button className={styles.sortButton} onClick={sortContacts}>
        Sort Contacts
      </button>
      <ul className={styles.contactList}>
        {contacts.map((contact) => (
          <li key={contact.id} className={styles.contactItem} data-testid="contactItem">
            {contact.name}{' '}
            <button className={styles.deleteButton} onClick={() => deleteContact(contact.id)}>
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Contacts;
