import React, { useState, useEffect } from 'react';
import styles from './PhoneInCall.module.css';

function PhoneInCall() {
  const [isInCall, setIsInCall] = useState(false);

  // Mock function to change call state
  const toggleCallStatus = () => setIsInCall(!isInCall);

  useEffect(() => {
    async function checkInCallStatus() {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
        const tracks = stream.getTracks();
        const isAudioInUse = tracks.some(
          (track) => track.kind === 'audio' && track.readyState === 'live'
        );
        const isVideoInUse = tracks.some(
          (track) => track.kind === 'video' && track.readyState === 'live'
        );
        setIsInCall(isAudioInUse && !isVideoInUse);
        tracks.forEach((track) => track.stop());
      } catch (error) {
        setIsInCall(false);
      }
    }

    checkInCallStatus();
  }, []);

  return (
    <div className={styles.container}>
      <svg
        xmlns="http://www.w3.org/2000/svg"
        viewBox="0 0 24 24"
        className={styles.phoneIcon}
      >
        <path
          fill="currentColor"
          d="M15.013 18.492c-.045-.047-2.33-2.394-3.057-3.067-1.216-1.203-1.348-1.674-1.42-3.054-.113-2.287 1.695-4.102 3.982-4.217 1.372-.067 1.84.089 3.062 1.306 1.22 1.21 1.25 1.672 1.306 3.044.118 2.348-1.724 4.203-4.273 4.238zm-3.849-15.185c-1.867 0-3.38 1.513-3.38 3.38 0 1.867 1.513 3.38 3.38 3.38 1.867 0 3.38-1.513 3.38-3.38 0-1.867-1.513-3.38-3.38-3.38zm0 14.161c2.717 0 4.909-2.192 4.909-4.909 0-2.716-2.192-4.909-4.909-4.909-2.716 0-4.909 2.193-4.909 4.91 0 2.717 2.193 4.909 4.91 4.909z"
        />
      </svg>
      <h2 className={styles.callStatus}>
        {isInCall ? 'The device is in call' : 'The device is not in call'}
      </h2>
      <button className={styles.toggleButton} onClick={toggleCallStatus}>
        Toggle Call Status
      </button>
    </div>
  );
}

export default PhoneInCall;
