import React, { useState, useEffect } from 'react';
import styles from './Contacts.module.css';
import { v4 as uuidv4 } from 'uuid';

function Contacts() {
  const [contacts, setContacts] = useState([]);
  const [contactsFetched, setContactsFetched] = useState(false);
  const [newContactName, setNewContactName] = useState('');
  const [showAddContact, setShowAddContact] = useState(false);

  useEffect(() => {
    fetch('/contacts.json')
      .then((response) => response.json())
      .then((data) => {
        setContacts(data);
        setContactsFetched(true);
      })
      .catch((error) => console.error('Error fetching contacts:', error));
  }, []);

  const deleteContact = (index) => {
    const updatedContacts = contacts.filter((_, i) => i !== index);
    setContacts(updatedContacts);
  };

  const addContact = () => {
    if (newContactName.trim() === '') {
      alert('Please enter a contact name.');
      return;
    }

    const newContact = {
      id: uuidv4(),
      name: newContactName,
    };

    setContacts([newContact, ...contacts]);
    setNewContactName('');
    setShowAddContact(false);
  };

  const cancelAddContact = () => {
    setShowAddContact(false);
    setNewContactName('');
  };

  return (
    <div className={styles.contacts}>
      <h1 className={styles.title}>Contacts</h1>
      {contactsFetched && (
        <>
          <div className={styles.addContactContainer}>
            {showAddContact ? (
              <>
                <input
                  className={styles.addContactInput}
                  type="text"
                  placeholder="Enter contact name"
                  value={newContactName}
                  onChange={(e) => setNewContactName(e.target.value)}
                />
                <button className={styles.addContactButton} onClick={addContact}>
                  Confirm
                </button>
                <button className={styles.cancelButton} onClick={cancelAddContact}>
                  Cancel
                </button>
              </>
            ) : (
              <button className={styles.addButton} onClick={() => setShowAddContact(true)}>
                Add Contact
              </button>
            )}
          </div>
          <ul className={styles.contactList}>
            {contacts.map((contact, index) => (
              <li key={index} className={styles.contactItem}>
                {contact.name}{' '}
                <button className={styles.deleteButton} onClick={() => deleteContact(index)}>
                  Delete
                </button>
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
}

export default Contacts;
